import{e as p}from"./app.d1ef40ff.js";import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";const e={},r=p('<h1 id="\u8BA1\u5212\u751F\u80B2\u5BA3\u4F20\u6807\u8BED" tabindex="-1"><a class="header-anchor" href="#\u8BA1\u5212\u751F\u80B2\u5BA3\u4F20\u6807\u8BED" aria-hidden="true">#</a> \u8BA1\u5212\u751F\u80B2\u5BA3\u4F20\u6807\u8BED</h1><p>\u6E56\u5357\u67D0\u5730\u7684\u201C\u8D85\u751F\u5C31\u624E\uFF01\u201D\uFF0C\u597D\u4E00\u4E2A\u5E72\u8106\u51B3\u7EDD\u7684\u6781\u5DE6\u5BA3\u4F20\u6807\u8BED\u3002</p><p>\u4F46\u6BD4\u8D77\u4E91\u5357\u695A\u96C4\u67D0\u519C\u6751\u7684\u201C\u4E00\u4EBA\u8D85\u751F\uFF0C\u5168\u6751\u7ED3\u624E\uFF01\u201D\u5C31\u7B97\u4E0D\u4E86\u4EC0\u4E48\u4E86\u3002</p><p>\u5C71\u4E1C\u7684\u56FD\u9053\u4E0A\u6709\u6761\u5BA3\u4F20\u6807\u8BED\u7EDD\u5BF9\u7ECF\u5178\uFF1A\u201C\u4E00\u4EBA\u7ED3\u624E\uFF0C\u5168\u5BB6\u5149\u8363\u201D\uFF0C\u773C\u524D\u4EFF\u4F5B\u6D6E\u73B0\u51FA\u4E00\u5E55\uFF1A\u653E\u97AD\u70AE\u3001\u9A91\u5927\u9A6C\u3001\u6234\u7EA2\u82B1\uFF0C\u9022\u4EBA\u4FBF\u8DBE\u9AD8\u6C14\u6602\u5730\u5BA3\u5E03\uFF1A\u201C\u4FFA\u7ED3\u624E\uFF01\u5FEB\u6765\u4FFA\u5BB6\u559D\u9152\u5427\uFF01\u3002\u201D</p><p>\u91CD\u5E86\u67D0\u4E61\u8DEF\u8FB9\u7684\u519C\u820D\u4E0A\u7528\u767D\u6F06\u5237\u7740\uFF1A\u201C\u8BE5\u624E\u4E0D\u624E\uFF0C\u89C1\u4E86\u5C31\u6293\u3002\u201D</p><p>\u67D0\u5730\u5012\u6709\u70B9\u91CD\u5973\u8F7B\u7537\uFF1A\u201C\u5973\u624E\u8981\u5F97\u75C5\uFF0C\u7537\u624E\u8FD8\u80FD\u884C\uFF01\u201D</p><p>\u5728\u6D59\u6E90\u81F3\u7406\u5751\u7684\u8DEF\u4E0A\u4E00\u5C0F\u6751\u5E84\uFF1A\u201C\u56FD\u5BB6\u5174\u65FA\uFF0C\u5339\u592B\u6709\u8D23\uFF1B\u8BA1\u5212\u751F\u80B2\uFF0C\u4E08\u592B\u6709\u8D23\u201D\u3002\u8FD9\u8BF4\u660E\u90A3\u91CC\u7684\u4E3B\u8981\u969C\u788D\u5728\u7537\u65B9\u3002</p><p>\u5728\u6C5F\u897F\u5A7A\u539F\u89C1\u5230\u7684\u7684\u5BA3\u4F20\u6807\u8BED\uFF1A\u201C\u89C1\u8BC1\u6000\u5B55\uFF0C\u6301\u8BC1\u751F\u80B2\uFF01\u201D</p><p>\u5C71\u4E1C\u67D0\u519C\u6751\u8BA1\u5212\u751F\u80B2\u5BA3\u4F20\u6807\u8BED\uFF1A\u201C\u80FD\u5F15\u5730\u5F15\u51FA\u6765\uFF0C\u80FD\u6D41\u5730\u6D41\u51FA\u6765\uFF0C\u575A\u51B3\u4E0D\u80FD\u751F\u4E0B\u6765\u3002\u201D</p><p>\u65C1\u8FB9\u8FD8\u6709\u4E00\u6761\uFF1A\u201C\u666E\u53CA\u4E00\u80CE\uFF0C\u63A7\u5236\u4E8C\u80CE\uFF0C\u6D88\u706D\u4E09\u80CE\u3002\u201D</p><p>\u5C71\u4E1C\u83CF\u6CFD\u7684\u8BA1\u5212\u751F\u80B2\uFF1A\u201C\u5B81\u53EF\u5BB6\u7834\uFF0C\u4E0D\u53EF\u56FD\u4EA1\u3002\u201D</p><p>\u5B89\u5FBD\u67D0\u53BF\uFF1A\u201C\u5B81\u6DFB\u5341\u5EA7\u575F\uFF0C\u4E0D\u6DFB\u4E00\u4E2A\u4EBA\u3002\u201D</p><p>\u6C5F\u82CF\u519C\u6751\u591A\u5904\u53EF\u89C1\u7684\u5BA3\u4F20\u6807\u8BED\uFF1A\u201C\u5B81\u53EF\u8840\u6D41\u6210\u6CB3\uFF0C\u4E0D\u51C6\u8D85\u751F\u4E00\u4E2A\u3002\u201D</p><p>\u6E56\u5357\u67D0\u53BF\u7684\u751F\u80B2\u8BA1\u5212\u6807\u8BED\uFF1A\u201C\u8C01\u4E0D\u5B9E\u884C\u8BA1\u5212\u751F\u80B2\uFF0C\u5C31\u53EB\u4ED6\u5BB6\u7834\u4EBA\u4EA1\u3002\u201D</p><p>\u56DB\u5DDD\u67D0\u5C71\u6751\uFF1A\u201C\u4E00\u80CE\u751F\uFF0C\u4E8C\u80CE\u624E\uFF0C\u4E09\u80CE\u56DB\u80CE--\u522E\uFF01\u522E\uFF01\u522E\uFF01\u201D</p><p>\u5E7F\u897F\u524D\u5F80\u5FB7\u5929\u7011\u5E03\u7684\u8DEF\u4E0A\u6709\u6761\u66F4\u72E0\uFF1A\u201C\u4E00\u80CE\u73AF\uFF0C\u4E8C\u80CE\u624E\uFF0C\u4E09\u80CE\u56DB\u80CE\u6740\u6740\u6740\uFF01\u201D</p><p>\u5728\u6CB3\u5357\u7684\u5BA3\u4F20\u6807\u8BED\u66F4\u6709\u90AA\u95E8\u7684\uFF0C\u53EB\u505A\uFF1A\u201C\u8BE5\u624E\u4E0D\u624E\uFF0C\u623F\u5012\u5C4B\u584C\uFF1B\u8BE5\u6D41\u4E0D\u6D41\uFF0C\u6252\u623F\u7275\u725B\u3002\u201D\u8DB3\u89C1\u90A3\u91CC\u7684\u8BA1\u751F\u5DE5\u4F5C\u4E4B\u8270\u96BE\u3002</p><p>\u67D0\u5730\u4F30\u8BA1\u8BA1\u5212\u751F\u80B2\u963B\u529B\u8F83\u5927\uFF0C\u6709\u5173\u90E8\u95E8\u4FBF\u5237\u4E86\u6761\u6807\u8BED\uFF1A\u201C\u559D\u836F\u4E0D\u593A\u74F6\uFF0C\u4E0A\u540A\u5C31\u7ED9\u7EF3\u3002\u201D</p><p>\u6E56\u5317\u9633\u65B0\u5730\u533A\u7684\u8BA1\u5212\u751F\u80B2\u6807\u8BED\u4E5F\u5F88\u5F62\u8C61\u5F88\u5F6A\u608D\uFF1A\u201C\u901A\u4E0D\u901A\uFF0C\u4E09\u5206\u949F\uFF1B\u518D\u4E0D\u901A\uFF0C\u9F99\u5377\u98CE\uFF01\u201D</p><p>\u5B89\u5FBD\u67D0\u5730\uFF1A\u201C\u575A\u51B3\u6253\u51FB\u6D41\u4EA7\u5973\u5A74\uFF01\u201D\u597D\u4E00\u6761\u4E2D\u56FD\u7279\u8272\u7684\u6807\u8BED\u3002</p><p>\u8D35\u5DDE\u7684\u5BA3\u4F20\u6807\u8BED\u5012\u662F\u5A49\u8F6C\u6E29\u67D4\uFF1A\u201C\u670B\u53CB\uFF0C\u4F60\u8BA1\u5212\u751F\u80B2\u4E86\u5417\uFF1F\u3002\u201D</p><p>\u5317\u4EAC\u67D0\u8FDC\u90CA\u533A\uFF1A\u201C\u5C11\u751F\u5B69\u5B50\u591A\u79CD\u6811\uFF0C\u5C11\u751F\u5B69\u5B50\u591A\u517B\u732A\uFF01\u3002\u201D</p><p>\u5728\u5C71\u897F\u770B\u5230\u7684\u7248\u672C\u662F\uFF1A\u201C\u5C71\u533A\u4EBA\u6C11\u8981\u60F3\u5BCC\uFF0C\u5C11\u751F\u5B69\u5B50\u591A\u79CD\u6811\u3002\u201D</p><p>\u6700\u7EDD\u7684\u662F\u4E1C\u4E09\u7701\u7684\u5BA3\u4F20\u6807\u8BED\uFF1A\u201C\u519C\u6751\u60F3\u4E0D\u7A77\uFF0C\u5C11\u751F\u5B69\u5B50\u517B\u72D7\u718A\u3002\u201D</p><p>\u6E56\u5357\u67D0\u4E61\u653F\u5E9C\u7684\u5BA3\u4F20\u6807\u8BED\uFF1A\u201C\u7ED3\u8D2B\u7A77\u7684\u624E\uFF0C\u4E0A\u81F4\u5BCC\u7684\u73AF\u3002\u201D</p><p>\u6E56\u5317\u67D0\u706B\u846C\u573A\u95E8\u53E3\uFF1A\u201C\u7ECF\u6D4E\u641E\u4E0A\u53BB\uFF0C\u4EBA\u53E3\u964D\u4E0B\u6765\u3002\u201D</p>',26);function t(n,c){return r}var s=a(e,[["render",t]]);export{s as default};
